    private static void bubbleSort(int[] array1) {
        Instant start = Instant.now();

        for (int lastUnsortedIndex = array1.length-1; lastUnsortedIndex > 0; lastUnsortedIndex--) {
            for (int i = 0; i < lastUnsortedIndex; i++) {
                if (array1[i] > array1[i+1]) {
                    swap(array1, i, i+1);
                }
            }
        }

        Instant finish = Instant.now();
        long timeElapsed = Duration.between(start, finish).getNano();
        System.out.printf("%d nanoseconds \n", timeElapsed);

        System.out.println(Arrays.toString(array1));
    }

    public static void anotherSort(int[] array) {
        Instant start = Instant.now();

        boolean flag = true;
        while(flag) {
            flag = false;
            for (int i=0; i<array.length-1; i++) {
                if (array[i] > array[i+1]) {
                    int temp = array[i];
                    array[i] = array[i+1];
                    array[i+1] = temp;
                    flag = true;
                }
            }
        }

        Instant finish = Instant.now();
        long timeElapsed = Duration.between(start, finish).getNano();
        System.out.printf("%d nanoseconds - Another Sort \n", timeElapsed);

        System.out.println(Arrays.toString(array));
    }

    public static void anotherSortWithCounter(int[] array) {

        Instant start = Instant.now();

        boolean flag = true;
        int counter = 0;
        while(flag) {
            flag = false;
            for (int i=0; i<array.length - 1 - counter; i++) {
                if (array[i] > array[i+1]) {
                    int temp = array[i];
                    array[i] = array[i+1];
                    array[i+1] = temp;
                    flag = true;
                }
            }
            counter++;
        }

        Instant finish = Instant.now();
        long timeElapsed = Duration.between(start, finish).getNano();
        System.out.printf("%d nanoseconds - Another Sort with Counter \n", timeElapsed);

        System.out.println(Arrays.toString(array));
    }

    private static void swap(int[] intArray, int i, int j) {
        int temp = intArray[i];
        intArray[i] = intArray[j];
        intArray[j] = temp;
    }
